import _ from 'underscore';
import Item from './item.js';
import misc from '../misc.js';
// import chalk from 'chalk';

export default class AudioFile extends Item {
  /** @ignore */
  static getSlots() {
    return super.getSlots().concat(['externalId', 'frames', 'channels', 'sampleRate', 'externalUrl', 'fileType']);
  }

  // inspect() {
  //   return chalk.magenta('<') + chalk.magenta.bold(this.pitch.toString()) + chalk.magenta('>');
  // },

  /** @ignore */
  init() {
    super.init();
    /** @type {Number} */
    this.frames = misc.parseIntOr(this.frames, 0);
    /** @type {Number} */
    this.channels = misc.parseIntOr(this.channels, null);
    /** @type {Number} */
    this.sampleRate = misc.parseFloatOr(this.sampleRate, null);
    return this;
  }

  /** @ignore */
  doValidate() {
    var validator = super.doValidate();
    validator.isInteger(this, 'frames', 1, null);
    validator.isInteger(this, 'channels', 1, 8);
    validator.isNumber(this, 'sampleRate', 22050, 192000);
    return validator;
  }
}

AudioFile.coerce = function(source, parent, copy) {
  if (source instanceof AudioFile) {
    if (copy) return new AudioFile(source, parent);
    else return source;
  }
  if (_.isObject(source)) {
    return new AudioFile(source, parent);
  }
  throw new Error('Cannot coerce ' + source + ' to an audio file!');
};

AudioFile.itemType = 'AudioFile';

import ItemHandler from '../item-handler.js';
ItemHandler.registerItem(AudioFile);

import Item from './item.js';
import misc from '../misc.js';
import Fraction from 'fraction.js';
import _ from 'underscore';
var isNumber = _.isNumber;
var isObject = _.isObject;

/* eslint-disable */
var wnToVexFlow = {
  '7/4':   { duration: 'w',  dots: 2 },  // "noteValue" would be better than "duration",
  '3/2':   { duration: 'w',  dots: 1 },  // but let's follow VexFlow's nomenclature
  '1/1':   { duration: 'w',  dots: 0 },
  '7/8':   { duration: 'h',  dots: 2 },
  '3/4':   { duration: 'h',  dots: 1 },
  '1/2':   { duration: 'h',  dots: 0 },
  '7/16':  { duration: 'q',  dots: 2 },
  '3/8':   { duration: 'q',  dots: 1 },
  '1/4':   { duration: 'q',  dots: 0 },
  '7/32':  { duration: '8',  dots: 2 },
  '3/16':  { duration: '8',  dots: 1 },
  '1/8':   { duration: '8',  dots: 0 },
  '7/64':  { duration: '16', dots: 2 },
  '3/32':  { duration: '16', dots: 1 },
  '1/16':  { duration: '16', dots: 0 },
  '7/128': { duration: '32', dots: 2 },
  '3/64':  { duration: '32', dots: 1 },
  '1/32':  { duration: '32', dots: 0 },
  // '7/256': { duration: '64', dots: 2 },
  '3/128': { duration: '64', dots: 1 },
  '1/64':  { duration: '64', dots: 0 },
  // '7/512': { duration: '128', dots: 2 },
  // '3/256': { duration: '128', dots: 1 },
  '1/128': { duration: '128', dots: 0 },
}
/* eslint-enable */

export default class Duration extends Item {
  /**
   * @ignore
   */
  static getSlots() {
    return super.getSlots().concat(['value', 'unit']);
  }

  /**
   * @ignore
   */
  inspect() {
    // return chalk.blue.bold(this.value.toFraction()) + ' ' + chalk.blue(this.unit);
  }

  /**
   * @ignore
   */
  init() {
    super.init();
    /** @type {Fraction} */
    this.value = new Fraction(this.value || 0);
    /** @type {String} */
    this.unit = this.unit || 'ms';
    return this;
  }

  /**
   * Is this a zero duration?
   * @return {boolean}
   */
  isZero() {
    // eslint-disable-next-line eqeqeq
    return this.value == 0; // N.B: Don't use ===
  }

  /**
   * Returns `true` if the duration is in time units (currently `s` or `ms`), `false` otherwise.
   * @return {boolean}
   */
  isTime() {
    return ['s', 'ms'].indexOf(this.unit) >= 0;
  }

  /**
   * Returns `true` if the duration is in atomic units, `false` otherwise.
   * The inverse of {@link isTime()}
   * @return {boolean}
   */
  isAtom() {
    return !this.isTime();
  }

  /**
   * Returns `true` if the duration has a fixed unit, i.e. unaffected by time signature.
   * Currently, the fixed units are `s`, `ms` and `wn`.
   * @return {boolean}
   */
  isFixed() {
    return ['s', 'ms', 'wn'].indexOf(this.unit) >= 0;
  }

  /**
   * Returns a new duration with same unit and inverted value
   * (positive durations become negative and v.v.)
   * The current object is not changed.
   * @return {Duration}
   */
  inverse() {
    return new Duration({value: this.value.neg(), unit: this.unit});
  }

  /**
   * Return the duration as a number of seconds.
   * Valid only when the duration is expressed in time units (`s` or `ms`)
   * @return {Fraction}
   */
  toSeconds() {
    switch (this.unit) {
      case 's': return this.value;
      case 'ms': return this.value.div(1000);
    }
  }

  /**
   * Return the value of the duration as a floating point number (ignoring the unit).
   * @return {Number}
   */
  toFloat() {
    return this.value.valueOf();
  }

  /** @ignore */
  doValidate() {
    var validator = super.doValidate();
    validator.hasType(this, 'value', Fraction);
    validator.isOneOf(this, 'unit', ['s', 'ms', 'measures', 'beats', 'atoms', 'wn']);
    return validator;
  }

  /** @ignore */
  toString() {
    return this.value.toFraction() + ' ' + this.unit;
  }

  /** @ignore */
  toJSON() {
    if (this.value.n === 0) return 0;
    if (this.value.d === 1) return [this.value.n * this.value.s, this.unit];
    return [this.value.n * this.value.s, this.value.d, this.unit];
  }

  /** @ignore */
  toXML(rootName) {
    return misc.buildXML(this.toXMLObject(), {rootName: rootName || 'duration'});
  }

  /** @ignore */
  toXMLObject() {
    if (this.unit === '' || this.unit === 'divisions') return {_: this.num / this.denom};
    return {_: this.num / this.denom, '$': {unit: this.unit}};
  }

  /** @ignore */
  toVexFlow() {
    if (this.unit === 'wn') {
      return Duration.wnToVexFlow(this.value);
    }
  }

  /** @ignore */
  static wnToVexFlow(fraction) {
    return wnToVexFlow[fraction.n + '/' + fraction.d];
  }
}

Duration.fromString = function(str, parent) {
  var matches = str.match(/^([0-9\.]+(?:\/[1-9][0-9]*)?)\s*([A-Za-z]*)$/);
  if (matches) {
    return new Duration({value: matches[1], unit: matches[2]}, parent);
  }
};

Duration.coerce = function(source, parent, copy) {
  if (source instanceof Duration) {
    if (copy) return new Duration(source, parent);
    else return source;
  }
  if (typeof source === 'string') return Duration.fromString(source, parent);
  if (isNumber(source)) return new Duration({value: source}, parent);
  if (source instanceof Array && source.length === 2) return new Duration({value: source[0], unit: source[1]}, parent);
  if (source instanceof Array && source.length === 3) return new Duration({value: [source[0], source[1]], unit: source[2]}, parent);
  if (isObject(source)) return new Duration(source, parent);
  throw new Error('Cannot coerce ' + source + ' to a duration!');
};

Duration.fromXML = function(xml) {
  var obj = misc.parseXML(xml, {explicitArray: false, mergeAttrs: true, explicitCharkey: true});
  return Duration.fromXMLObject(obj);
};

Duration.fromXMLObject = function(obj) {
  if (typeof obj === 'string') return new Duration(parseInt(obj), 'divisions');
  return new Duration({num: parseInt(obj._), unit: obj.unit || 'divisions'});
};

Duration.itemType = 'Duration';

import ItemHandler from '../item-handler.js';
ItemHandler.registerItem(Duration);

import _ from 'underscore';
import Event from './event.js';
import Duration from '../duration.js';

/**
 * An audio event, playing back audio from e.g. an audio file
 */
export default class Audio extends Event {
  /** @ignore */
  static getSlots() {
    return super.getSlots().concat(['file', 'trimLeft', 'trimRight']);
  }

  /** @ignore */
  init() {
    super.init();
    this.trimLeft = this.initSubObject(this.trimLeft, Duration);
    this.trimRight = this.initSubObject(this.trimRight, Duration);
    return this;
  }

  /** @ignore */
  doValidate() {
    var validator = super.doValidate();
    validator.hasTypeOrNull(this, 'trimLeft', Duration);
    validator.hasTypeOrNull(this, 'trimRight', Duration);
    validator.hasTypeOrNull(this, 'file', Duration);
    return validator;
  }
}

Audio.coerce = function(source, parent, copy) {
  if (source instanceof Audio) {
    if (copy) {
      return new Audio(source, parent);
    } else {
      return source;
    }
  }
  if (_.isObject(source)) {
    return new Audio(source, parent);
  }
  throw new Error('Cannot coerce ' + source + ' to an audio object!');
};

Audio.itemType = 'Audio';

import ItemHandler from '../../item-handler.js';
ItemHandler.registerItem(Audio);

import _ from 'underscore';

import EventContainer from './event-container.js';
import Pitch from '../pitch.js';
import Interval from '../interval.js';

function fractionToWnJSON(fraction) {
  return [fraction.n, fraction.d, 'wn'];
}

function getChordPitches(chord) {
  var root = chord.initSubObject(chord.harmony.root, 'Pitch');
  var pitches = [root];

  _.each(chord.harmony.intervals, (interval) => {
    pitches.push(root.interval(interval));
  });

  return pitches;
}

function getChordMidiNumbers(chord) {
  return _.map(getChordPitches(chord), (pitch) => {
    return pitch.toMIDI();
  });
}

function findClosestMidiNumber(targetNumber, inputNumber) {
  var difference = targetNumber - inputNumber;
  var octaveDifference = Math.round(difference / 12);
  return inputNumber + (octaveDifference * 12);
}

function findClosestChordMidiNumber(targetNumber, chord) {
  var chordNumbers = getChordMidiNumbers(chord);

  var currentClosest = null;
  var currentDistance = null;

  _.each(chordNumbers, (n) => {
    var closest = findClosestMidiNumber(targetNumber, n);
    var distance = Math.abs(targetNumber - closest);

    if (!currentClosest || distance < currentDistance) {
      currentClosest = closest;
      currentDistance = distance;
    }
  });

  return currentClosest;
}

function closestOctaveDifference(pitchFrom, pitchTo) {
  var mod = (a, b) => (a % b + b) % b;

  // calculate closest direction to transpose
  var interval = pitchTo.interval(pitchFrom).coord[1];
  var octaveDifference = Math.sign(interval) * Math.abs(Math.floor(interval / 12));

  var chromaticDifference = mod(interval, 12);
  if (chromaticDifference > 6) {
    chromaticDifference = chromaticDifference - 12;
  }

  // is closest new root in the same octave?
  var octaveCorrection = Math.floor(pitchFrom.coord[1] / 12) - Math.floor((pitchFrom.coord[1] + chromaticDifference) / 12);

  // adjust
  octaveDifference += octaveCorrection;

  return octaveDifference;
}

function transposeNote(keyFrom, keyTo, note) {
  var pitch = note.pitch;

  var octaveDifference = closestOctaveDifference(keyFrom.root, keyTo.root);
  var octaveInterval = new Interval({coord: [octaveDifference * 7, octaveDifference * 12]});

  // transpose destination root pitch to be in closest octave

  var keyInterval = keyFrom.root.interval(keyTo.root);
  keyInterval = keyInterval.add(octaveInterval);

  // this mutates the original note rather than creating a new one, correct?
  pitch.transpose(keyInterval);

  return note;
}

function noteToAutoNote(env, chord, note) {
  var targetNumber = note.pitch.toMIDI();
  var result = findClosestChordMidiNumber(targetNumber, chord);

  if (result) {
    note.pitch = Pitch.fromMIDI(result, note);
  } else {
    note.amp = 0;
  }
}

function noteToChordNote(env, chord, note) {
  var step = env.harmonyRelation.step;

  if (!step) {
    console.error('"step" is required in harmonyRelation when using type "chordNote"!');
    note.amp = 0;
    return;
  }

  var chordNumbers = getChordMidiNumbers(chord);
  var index = (step - 1) % chordNumbers.length;
  var chordNoteNumber = chordNumbers[index];

  var targetNumber = note.pitch.toMIDI();
  var result = findClosestMidiNumber(targetNumber, chordNoteNumber);

  if (result) {
    note.pitch = Pitch.fromMIDI(result, note);
  } else {
    note.amp = 0;
  }
}

function noteToKeyNote(env, note) {
  var patternKey = env.patternKey;
  var songKey = env.songKey;

  return transposeNote(patternKey, songKey, note);
}

function getInstruments(song) {
  return (
    _.chain(song.findEvents('Voice'))
    .map(function(voice) { return voice.sound; })
    .filter(_.identity)
    .unique()
    .value()
  );
}

function generateChordPlayback(chordSequence, patternSong) {
  var song = chordSequence.env.get('song');
  var songKey = song.findMeta('Key');
  var chords = chordSequence.findEvents('Chord');
  var patternKey = patternSong.findMeta('Key');

  // make sure pattern is 1 bar long
  var pattern = (
    _.chain(patternSong.findEvents('Note'))
    .filter((note) => {
      var absWn = note.cache.absWn.valueOf();
      return absWn >= 0 && absWn < 1;
    })
    // We decided against transposing all notes, instead it only transposes key notes.
    // .map((note) => {
    //   return transposeNote(patternKey, songKey, note);
    // })
    .value()
  );

  // sort chords by time
  chords = _.sortBy(chords, (chord) => {
    return chord.cache.absTime.valueOf();
  });

  var chordEndWn = _.last(chords).cache.endWn;
  var measures = _.times(Math.ceil(chordEndWn.valueOf()), _.identity);
  var events = [];

  _.each(measures, (measure) => {
    var newEvents = _.map(pattern, (note) => {
      var out = note.toJSON();

      out.position = fractionToWnJSON(note.cache.absWn.add(measure));
      out.duration = fractionToWnJSON(note.cache.absWn.sub(note.cache.endWn));

      return out;
    });

    events = events.concat(newEvents);
  });

  var instruments = getInstruments(patternSong);

  if (instruments.length > 1) {
    console.error('Pattern contains more than 1 instrument!' +
      ' Only the first one will be used. Instruments found:', instruments);
  }

  var container = song.initSubObject({
    type: 'Voice',
    id: 'generated-chords',
    eventTimeMode: 'absolute',
    sound: instruments[0] || 'keyboard',
    events: events
  });

  container.resolve(true);

  var env = {
    song: song,
    patternSong: patternSong,
    chords: chords,
    songKey: songKey,
    patternKey: patternKey
  };

  var currentChordIndex = null;
  var currentChord = null;

  _.each(container.events, (event) => {
    if (!currentChord) {
      // https://github.com/infusion/Fraction.js/#boolean-comparen
      if (event.cache.absWn.compare(chords[0].cache.absWn) >= 0) {
        currentChord = chords[0];
        currentChordIndex = 0;
      }
    }

    var nextChordIndex = currentChordIndex + 1;
    var nextChord = chords[nextChordIndex];

    if (nextChord && event.cache.absWn.compare(nextChord.cache.absWn) >= 0) {
      currentChordIndex = nextChordIndex;
      currentChord = nextChord;

      nextChordIndex = currentChordIndex + 1;
      nextChord = chords[nextChordIndex];
    }

    if (!currentChord) {
      event.amp = 0;
      return event;
    }

    var harmonyRelation = _.extend({
      type: 'auto'
    }, event.harmonyRelation);

    env.currentChord = currentChord;
    env.nextChord = nextChord;
    env.harmonyRelation = harmonyRelation;

    var note = event;

    switch (harmonyRelation.type) {
      default:
      case 'auto': noteToAutoNote(env, currentChord, note); break;
      case 'keyNote': noteToKeyNote(env, note); break;
      case 'chordNote': noteToChordNote(env, currentChord, note); break;
    }
  });

  return container;
}

/**
 * A sequence of chords
 *
 * Convenience class to group chords horizontally, like a {@link Voice} but for {@link Chord}s
 * rather than for {@link Note}s or {@link NoteChord}s.
 *
 */
export default class ChordSequence extends EventContainer {
  /** @ignore */
  get defaultEventItemType() {
    return 'Chord';
  }

  /** @ignore */
  inspect() {
    // var color = chalk.yellow;
    // return color.bold('ChordSequence') + color(' [') + this.getEvents().join(', ') + color(']');
  }

  generateChordPlayback(patternSong) {
    return generateChordPlayback(this, patternSong);
  }
}

ChordSequence.coerce = function(source, parent, copy) {
  if (source instanceof ChordSequence) return copy ? new ChordSequence(source, parent) : source;
  throw new Error('Cannot coerce ' + source + ' to a chord sequence!');
};

ChordSequence.itemType = 'ChordSequence';

import ItemHandler from '../../item-handler.js';
ItemHandler.registerItem(ChordSequence);

import Event from './event.js';
// import chalk from 'chalk';
import Harmony from '../harmony.js';

/**
 * A chord event
 *
 * In remus terminology, a Chord is a Harmony in event form. In other words, Chord
 * relate to {@link Harmony} much in the same way as {@link Note} relate to {@link Pitch}.
 *
 */
export default class Chord extends Event {
  /** @ignore */
  static getSlots() {
    return super.getSlots().concat(['harmony']);
  }

  /** @ignore */
  inspect() {
    // var color = chalk.yellow;
    // if (this.harmony) return color('<') + color.bold(this.harmony) + color('>');
    // return color('<chord>');
  }

  /** @ignore */
  init() {
    super.init();
    /** @type {?Harmony} */
    this.harmony = this.initSubObject(this.harmony, Harmony) || null;
    this.isChord = true; // ?
    return this;
  }

  /** @ignore */
  toString() {
    if (this.harmony) return '<' + this.harmony.toString() + '>';
    return '<chord>';
  }

  /** @ignore */
  localResolve() {
    super.localResolve();
    if (this.harmony) {
      this.cache.harmonyKind = this.harmony.kind();
    }
  }
}

Chord.coerce = function(source, parent, copy) {
  if (source instanceof Chord) {
    if (copy) return new Chord(source, parent);
    else return source;
  }
  if (source instanceof Harmony) return new Chord({harmony: source}, parent);
  return new Chord(source, parent);
  // throw new Error('Cannot coerce ' + source + ' to a chord!');
};

Chord.itemType = 'Chord';

import ItemHandler from '../../item-handler.js';
ItemHandler.registerItem(Chord);

import _ from 'underscore';
import Event from './event.js';
import Note from './note.js';
import Duration from '../duration.js';
import ItemHandler from '../../item-handler.js';
import Fraction from 'fraction.js';
import misc from '../../misc.js';
import { createState, stepState, updateState } from '../../resolver.js';

/**
 * Base class for events that can contain other events.
 *
 * Event containers store their child events in two lists:
 * `events` and `metas`. The `events` list is for Notes, Rests, Chords,
 * Audio clips etc, while the `metas` list is used to store "meta-events"
 * like Time, Key and Tempo.
 *
 * EventContainers can be nested, e.g. a Song could contain a voice, which
 * in turn contains a NoteChord, containing some Notes.
 *
 * **Note** that `metas` cannot be nested. Events in `metas` also cannot
 * have (non-zero) duration  they only have position.
 *
 * All event containers have a *time mode* for its `events` and `metas` lists.
 * Time mode can be either `absolute` or `relative`. This determines how
 * the position of the contained events are interpreted.
 *
 * * In **absolute time mode**, positions are related to the parent container's start position
 * * In **relative time mode**, positions are related to the previous event's end position
 *
 * Subclasses of EventContainer specify a default time mode, however that can be
 * overridden in individual objects if needed.
 *
 * For example, VerticalContainer have the default `eventTimeMode` set to `absolute`.
 * This means that Notes in a NoteChord relate to the beginning of the NoteChord by default,
 * while NoteChords in a Voice are placed after each other by default (since the default
 * `eventTimeMode` for Voice is `relative`).
 */
export default class EventContainer extends Event {
  /**
   * @ignore
   */
  static getSlots() {
    return super.getSlots().concat(['eventTimeMode', 'metaTimeMode', 'scale']);
  }

  /*
   * @ignore
   */
  constructor(properties, parent) {
    if (!properties) properties = {};
    super(properties, parent);

    this.initEvents(properties.events || [], this.defaultEventItemType); // Why not just specify events and metas as slots??
    this.metas = this.initSubObjects(properties.metas);
  }

  /** @ignore */
  initEvents(events, defaultItemType) {
    var ItemConstructor = defaultItemType ? ItemHandler.getConstructor(defaultItemType) : null;
    this.events = this.initSubObjects(events, ItemConstructor);
  }

  /** @ignore */
  init() {
    super.init();
    this.eventTimeMode = this.eventTimeMode || this.defaultEventTimeMode;
    this.metaTimeMode = this.metaTimeMode || this.defaultMetaTimeMode;
    /** @type {Fraction} */
    this.scale = this.scale === undefined ? null : new Fraction(this.scale);
    return this;
  }

  /** @ignore */
  clone() {
    let copy = super.clone();
    copy.events = misc.clone(this.events);
    copy.metas = misc.clone(this.metas);
    return copy;
  }

  /**
   * Returns immediate children of the specified class, or matching the specified function.
   * @param {?(string|function)} [selector] - A string, a function or `null` (for matching all items)
   * @return {Array}
   */
  childEvents(selector) {
    var selectorFunction = null;

    if (_.isFunction(selector)) {
      selectorFunction = selector;
    } else if (_.isString(selector)) {
      selectorFunction = (item) => {
        return item.type === selector;
      };
    } else {
      selectorFunction = v => true;
    }

    return _.filter(this.events, selectorFunction);
  }

  /**
   * Returns descendents of the specified class, or matching the specified function.
   * @param {?(string|function)} [selector] - A string, a function or `null` (for matching all items)
   * @return {Array}
   */
  findEvents(selector) {
    var events = this.childEvents(selector);

    _.each(this.events, (event) => {        // NB: Go through the unfiltered this.events, in order to search the whole tree
      if (event instanceof EventContainer) {
        events = events.concat(event.findEvents(selector));
      }
    });

    return events;
  }

  /**
   * Find the first descendent of the specified class, or matching the specified function.
   * @param {?(string|function)} [selector] - A string, a function or `null` (for matching all items)
   * @return {Event?}
   */
  findEvent(selector) {
    var events = this.findEvents(selector); // TODO: stop at first match instead of finding all
    return events ? events[0] : null;
  }

  /**
   * Returns immediate child metas of the specified class, or matching the specified function.
   * @param {?(string|function)} [selector] - A string, a function or `null` (for matching all items)
   * @return {Array}
   */
  childMetas(selector) {
    var selectorFunction = null;

    if (_.isFunction(selector)) {
      selectorFunction = selector;
    } else if (_.isString(selector)) {
      selectorFunction = (item) => {
        return item.type === selector;
      };
    } else {
      selectorFunction = v => true;
    }

    return _.filter(this.metas, selectorFunction);
  }

  /**
   * Returns descendent metas of the specified class, or matching the specified function.
   * @param {?(string|function)} [selector] - A string, a function or `null` (for matching all items)
   * @return {Array}
   */
  findMetas(selector) {
    var metas = this.childMetas(selector);

    _.each(this.events, (event) => {        // NB: Go through the unfiltered this.events, in order to search the whole tree
      if (event instanceof EventContainer) {
        metas = metas.concat(event.findMetas(selector));
      }
    });

    return metas;
  }

  /**
   * Find the first meta of the specified class, or matching the specified function.
   * @param {?(string|function)} [selector] - A string, a function or `null` (for matching all items)
   * @return {Event?}
   */
