    var newEventLists = [];
    _.each(this.events, (event) => {
      var layer = event.layer || 0;
      if (!newEventLists[layer]) newEventLists[layer] = [];
      newEventLists[layer].push(event);
      if (resetLayerSlot) event.layer = null;
    });
