/**
 * Base class for all Metas.
 */
export default class Meta extends Event {
  /**
   * @ignore
   */
  static getSlots() {
    return super.getSlots();
  }

  /**
   * @ignore
   */
  init() {
    super.init();
    return this;
  }

  /**
   * @ignore
   */
  doValidate() {
    var validator = super.doValidate();
    return validator;
  }
}

import Meta from './meta.js';
// import chalk from 'chalk';
import _ from 'underscore';
var isNumber = _.isNumber;
var isObject = _.isObject;
import Fraction from 'fraction.js';

/**
 * Tempo change meta-event.
 *
 * Tempo is expressed in `bpm` and `beat`, where `bpm` is a number
 * and `beat` is a wn Fraction. `beat` is coerced to a Fraction when
 * the item is created, but not if the slot is assigned at a later point.
 */
export default class Tempo extends Meta {
  /**
   * @ignore
   */
  static getSlots() {
    return super.getSlots().concat(['bpm', 'beat']);
  }

  /**
   * @ignore
   */
  inspect() {
    // var color = chalk.green;
    // return color('<') + color.bold(this.bpm.toString()) + color(' bpm>');
  }

  /**
   * @ignore
   */
  init() {
    super.init();

    /** @type {Fraction} */
    this.bpm = this.bpm || new Fraction(120);
    /** @type {Fraction} */
    this.beat = this.beat ? new Fraction(this.beat) : new Fraction(1, 4);

    // Temporary fix to prevent invalid remus output from JSON.stringify
    this.beat.toJSON = function() { return this.toFraction(); };
    return this;
  }

  /**
   * Return number of seconds per whole note for this tempo.
   * @return {Fraction}
   */
  sPerWn() {
    // return 60 / (this.bpm * this.beat);
    return (new Fraction(60)).div(this.beat.mul(this.bpm));
  }

  /**
   * @ignore
   */
  doValidate() {
    var validator = super.doValidate();
    // validator.isOneOf(this, 'beat', [1, 2, 4, 8, 16, 32, 64, 128, 256]);
    return validator;
  }

  /**
   * @ignore
   */
  toString() {
    return '<' + this.bpm.toString() + 'bpm>';
  }
}

Tempo.coerce = function(source, parent, copy) {
  if (source instanceof Tempo) {
    if (copy) return new Tempo(source, parent);
    else return source;
  }
  if (isNumber(source)) return new Tempo({bpm: source}, parent);
  if (isObject(source)) return new Tempo(source, parent);
  throw new Error('Cannot coerce ' + source + ' to a tempo!');
};

Tempo.itemType = 'Tempo';

import ItemHandler from '../../item-handler.js';
ItemHandler.registerItem(Tempo);

import Meta from './meta.js';
import misc from '../../misc.js';
import Fraction from 'fraction.js';
// import chalk from 'chalk';
import _ from 'underscore';
var isObject = _.isObject;

/**
 * Time signature meta-event.
 *
 * Time signature Tempo is expressed in `bpm` and `beat`, where `bpm` is a number
 * and `beat` is a wn Fraction. `beat` is coerced to a Fraction when
 * the item is created, but not if the slot is assigned at a later point.
 */
export default class Time extends Meta {
  /** @ignore */
  static getSlots() {
    return super.getSlots().concat(['beats', 'denom', 'abbreviate']);
  }

  /** @ignore */
  inspect() {
    // var color = chalk.green;
    // if (this.beats.every(function(x) { return x === 1 })) {
    //  return color('<') + color.bold(this.beats.length + '/' + this.denom) + color('>');
    // } else {
    // return color('<') + color.bold(this.beats.join('+') + '/' + this.denom) + color('>');
    // }
  }

  /** @ignore */
  init() {
    super.init();

    /** @type {number[]} */
    this.beats = this.beats || [1, 1, 1, 1];
    /** @type {number} */
    this.denom = this.denom || 4;
    /** @type {boolean} */
    this.abbreviate = !!this.abbreviate;

    if (_.isNumber(this.beats) && this.beats % 1 === 0 && this.beats > 0) {
      this.beats = Time.defaultBeatPattern(this.beats, this.denom);
      if (!this.beats) throw new Error('Could not find default beat pattern for ' + this.beats);
    } else if (!_.isArray(this.beats)) {
      throw new Error('Time.beats must be an array of integers');
    }

    return this;
  }

  getMeasureWn() {
    return new Fraction(this.num, this.denom);
  }

  /** @ignore */
  doValidate() {
    var validator = super.doValidate();
    validator.isArrayOfIntegers(this, 'beats');
    validator.isOneOf(this, 'denom', [1, 2, 4, 8, 16, 32, 64, 128, 256]);
    return validator;
  }

  /** @type {number} */
  get num() {
    return misc.sumArray(this.beats);
  }

  /** @ignore */
  toString() {
    return '<' + this.num + '/' + this.denom + '>';
  }

  /** @ignore */
  toVexFlow() {
    let str;
    if (this.abbreviate && this.num === 4 && this.denom === 4) {
      str = new String('C'); // eslint-disable-line no-new-wrappers
    } else if (this.abbreviate && this.num === 2 && this.denom === 2) {
      str = new String('C|'); // eslint-disable-line no-new-wrappers
    } else {
      str = new String(this.num + '/' + this.denom); // eslint-disable-line no-new-wrappers
    }
    str.id = this.id;
    return str;
  }

  /**
   * Get default beat pattern for a given numerator and denominator.
   * @param {number} num
   * @param {number} denom
   * @return {number[]}
   *
   * @example
   * Time.defaultBeatPattern(4, 4)  => [1, 1, 1, 1]
   * Time.defaultBeatPattern(6, 8)  => [3, 3]
   */
  static defaultBeatPattern(num, denom) {
    if (denom >= 8 && num >= 6 && num % 3 === 0) {
      let beats = Array(num / 3);
      for (let i = 0; i < beats.length; i++) beats[i] = 3;
      return beats;
    } else {
      let beats = Array(num);
      for (let i = 0; i < beats.length; i++) beats[i] = 1;
      return beats;
    }
