      if (accVal === undefined) return undefined;
      let tpo = this.env.get('tcuPerOctave') || defaults.tcuPerOctave;
      if (tpo === 12) {
        return knowledge.vexFlowAccidentals[accVal * 2 + 4] || '!';
      } else if (tpo % 24 === 0) {
