import ScoreMeta from './score-meta.js';
import ScoreClef from './score-clef.js';

export default class ScoreBar extends ScoreMeta {
  updatePosition() {
    super.updatePosition();
    let staffSpace = this.score.staffSize * 0.25;
    let staffSystem = this.score.staffSystems[this.staffSystem];
    this.y1 = this.y - 2 * staffSpace;
    this.y2 = this.y + 2 * staffSpace;
    if (this.leftBarline) {
      this.y2 += (staffSystem.staves.length - 1) * staffSystem.staffDistance;
    }
  }

  minSpace(nextItem) {
    if (nextItem instanceof ScoreClef) {
      return 0.5;
    } else {
      return 1;
    }
  }

  draw(ctx) {
    ctx.save();

    let staffSpace = this.score.staffSize * 0.25;

    ctx.lineCap = 'butt';
    ctx.lineWidth = 1.0;
    ctx.beginPath();
    ctx.moveTo(this.x, this.y - 2 * staffSpace);
    ctx.lineTo(this.x, this.y + 2 * staffSpace);
    ctx.stroke();
    ctx.restore();
  }

  toSVG(svg) {
    // let staffSpace = this.score.staffSize * 0.25;
    let barlineThickness = 1;
    let x = this.x + barlineThickness * 0.5;
    svg.line(x, this.y1, x, this.y2).stroke({width: barlineThickness});
  }
}

import ScoreItem from './score-item.js';

export default class ScoreBracket extends ScoreItem {
  constructor(item, score, params) {
    super(item, score, params);
    this.kind = this.kind || 'bracket';
    this.thickness = score.engravingDefaults.bracketThickness || 0.5;
  }

  updatePosition() {
    let staffSpace = this.score.staffSize * 0.25;
    let staffSystem = this.staffSystem;

    this.y1 = -2.5 * staffSpace;
    this.y2 = (staffSystem.staves.length - 1) * staffSystem.staffDistance + (staffSpace * 2.5);

    this.x = staffSystem.indent + staffSpace * ((this.xOffset || 0) - this.thickness + -0.25);
    this.y = this.y1 + staffSpace * (this.yOffset || 0);
  }

  toSVG(svg) {
    switch (this.kind) {
      case 'brace':
        svg.plain('\ue000').x(this.x).attr('y', this.y).font({family: null, size: null});
        break;
      case 'bracket':
        // let halfLineWidth = 0.5 * this.staffSystem.staffLineWidth;
        let thickness = this.thickness * this.score.staffSize * 0.25;
        let line = svg.line(this.x, this.y1, this.x, this.y2);
        line.stroke({width: thickness});
        let hookX = this.x - thickness * 0.5;
        svg.plain('\ue003').x(hookX).attr('y', this.y1).font({family: null, size: null});
        svg.plain('\ue004').x(hookX).attr('y', this.y2).font({family: null, size: null});
        break;
    }
  }
}

import ScoreMeta from './score-meta.js';
import knowledge from '../knowledge.js';

export default class ScoreClef extends ScoreMeta {
  constructor(item, score, params) {
    super(item, score, params);
    if (this.voice && this.voice.kind && (this.voice.kind === 'drums' || this.voice.kind === 'single-drum')) {
      this.kind = this.kind || 'unpitched1';
      this.glyphName = 'unpitchedPercussionClef1';
    } else {
      this.kind = this.kind || 'g';
      if (this.kind === 'unpitched1') {
        this.glyphName = 'unpitchedPercussionClef1';
      } else {
        this.glyphName = this.kind + 'Clef';
        if (this.octave === 1) this.glyphName += '8va';
        else if (this.octave === -1) this.glyphName += '8vb';
      }
    }
  }

  innerWidth() {
    let glyphData = this.score.musicFontMetadata.glyphBBoxes;
    let glyphName = this.glyphName;
    if (glyphData[glyphName]) {
      return glyphData[glyphName].bBoxNE[0];
    } else {
      console.log('Missing font metrics for ' + glyphName);
      return 3;
    }
  }

  visibleYPos() {
    switch (this.kind) {
      case 'g': return 1;
      case 'f': return -1;
      case 'c': return 0;
      default: return 0;
    }
  }

  toSVG(svg) {
    let smufl = knowledge.smuflCodepoints;
    svg.plain(smufl[this.glyphName]).x(this.x).attr('y', this.y).font({family: null, size: null});
  }
}

