import ScoreItem from './score-item.js';

export default class ScoreEvent extends ScoreItem {
  get fixedWidth() {
    return false;
  }
}

export default class ScoreItem {
  constructor(item, score, params) {
    this.item = item;
    this.score = score;
    for (let param in params) {
      this[param] = params[param];
    }
    this.leftExtent = 0;  // TEMP
    this.rightExtent = 0;
  }

  get floating() {
    return false;
  }

  get fixedWidth() {
    return true;
  }

  visibleYPos() {
    return 0;
  }

  innerWidth() {
    return 0;
  }

  minSpace(nextItem) {
    return 0.5;
  }

  flexibleSpace(nextItem) {
    return 0;
  }

  updateExtents() {
    this.leftExtent = 0;
    this.rightExtent = 0;
  }

  updatePosition() {
    let staffSpace = this.score.staffSize * 0.25;
    this.x = this.scoreX + staffSpace * (this.xOffset || 0);
    this.y = this.staffY + staffSpace * ((this.yOffset || 0) + this.visibleYPos());
  }

  updateBounds() {
    let staffSpace = this.score.staffSize * 0.25;
    let boundingBox = this.boundingBox();
    let scale = this.scale || 1;
    this.bounds = {
      minX: this.x + staffSpace * scale * boundingBox.left,
      minY: this.y + staffSpace * scale * boundingBox.top,
      maxX: this.x + staffSpace * scale * boundingBox.right,
      maxY: this.y + staffSpace * scale * boundingBox.bottom
    };
  }

  boundingBox() {
    return {
      left: -this.leftExtent,
      top: -2,
      right: this.innerWidth() + this.rightExtent,
      bottom: 2
    };
  }

  defaultColor() {
    if (this.selected) return 'blue';
    if (this.hovered) return 'red';
    return 'black';
  }

  draw(ctx) {
    console.log('Canvas drawing not implemented for ', this);
  }

  toSVG(svg) {
    console.log('SVG drawing not implemented for ', this);
  }
}

import ScoreMeta from './score-meta.js';
import ScoreTime from './score-time.js';
import ScoreNoteChord from './score-note-chord.js';
import scoreKnowledge from '../knowledge.js';
// import knowledge from '../../knowledge.js';

export default class ScoreKey extends ScoreMeta {
  constructor(item, score, params) {
    super(item, score, params);
    if (item) {
      this.root = item.root;
      this.mode = item.mode;
      this.fifths = this.root.fifths();
      let modeName = this.mode.getName();
      if (modeName === 'major') {
      } else if (modeName === 'minor') {
        this.fifths -= 3;
      }
    } else {
      this.root = null;
      this.mode = null;
      this.fifths = 0;
    }
    this.accidentals = [0, 0, 0, 0, 0, 0, 0];
    this.positions = [];
    if (this.fifths < 0) {
      this.positions = scoreKnowledge.keySignatures.flat.slice(0, -this.fifths);
      // TODO: don't hardcode, and don't limit to "simple" modes
      for (let n = 0; n < -this.fifths; n++) {
        // this.accidentals[['b', 'e', 'a', 'd', 'g', 'c', 'f'][n]] = -1;
        this.accidentals[[6, 2, 5, 1, 4, 0, 3][n]] = -1;
      }
    } else if (this.fifths > 0) {
      this.positions = scoreKnowledge.keySignatures.sharp.slice(0, this.fifths);
      for (let n = 0; n < this.fifths; n++) {
        // this.accidentals[['f', 'c', 'g', 'd', 'a', 'e', 'b'][n]] = 1;
        this.accidentals[[3, 0, 4, 1, 5, 2, 6][n]] = 1;
      }
    }
  }

  innerWidth() {
    return 1 * Math.abs(this.fifths);
  }

  minSpace(nextItem) {
    if (nextItem instanceof ScoreTime) {
      return 0.5;
    } else if (nextItem instanceof ScoreNoteChord) {
      return 2;
    } else {
      return 1;
    }
  }

  toSVG(svg) {
    let staffSize = this.score.staffSize;
    let staffSpace = staffSize * 0.25;
    if (this.fifths < 0) {
      for (let n = 0; n < -this.fifths; n++) {
        svg.smuflGlyph('accidentalFlat', this.x + n * staffSpace * 1, this.y - this.positions[n] * staffSpace * 0.5, staffSize);
      }
    } else if (this.fifths > 0) {
      for (let n = 0; n < this.fifths; n++) {
        svg.smuflGlyph('accidentalSharp', this.x + n * staffSpace * 1, this.y - this.positions[n] * staffSpace * 0.5, staffSize);
      }
    }
  }
}

import ScoreItem from './score-item.js';

export default class ScoreMeta extends ScoreItem {

}

import ScoreEvent from './score-event.js';

export default class ScoreNoteChord extends ScoreEvent {

  //  Calculates the default stem direction, the rule are the same
  //  regardles of if the notes are on the same stem or in the same beam:
  //  The note farthest away from the middle line determines the stem direction.
  //  in case of a tie, down is favoured.
  defaultStemDirection() {
    let min = this.notes[0].staffLine;
    let max = this.notes[this.notes.length - 1].staffLine;
    // for (let note of this.notes) {
    //   min = Math.min(min, note.staffLine);
    //   max = Math.max(max, note.staffLine);
    // }
    let furthest = (Math.abs(min) > Math.abs(max)) ? min : max;
    return furthest > 0 ? 'up' : 'down';
  }

  innerWidth() {
    return Math.max(...this.notes.map(n => n._notehead.bbox.bBoxNE[0]));
  }

  boundingBox() {
    return {
      left: -this.leftExtent,
      top: this.stem.direction === 'up' ? this.stem.end : this.stem.extent,
      right: this.innerWidth() + this.rightExtent,
      bottom: this.stem.direction === 'up' ? this.stem.extent : this.stem.end
    };
  }

  updateExtents() {
    let staffSpace = this.score.staffSize * 0.25;
    this.leftExtent = this.accidentalX || 0;
    let flagExtent = this.flags && !this.beam ? 1 * staffSpace : 0;
    let dotExtent = this.dots * staffSpace * this.score.musicFontMetadata.glyphBBoxes['augmentationDot'].bBoxNE[0] * 2;
    this.rightExtent = Math.max(flagExtent, dotExtent);
  }

/**
 *  Returns the y coordinate (in staff spaces) of the stem start (at a note head)
 *  > 1 notes and stem direction are taken into account
 *
 *  As a secondary value, returns the y coordinate of the outermost edge of the outermost notehead
 *  (usable for calculating the bounding box)
*/
  getStemStart() {
    let notes = this.notes;
    // TODO: use notehead metrics
    if (this.stem.direction === 'up') {
      let note = notes[notes.length - 1];
      let y = note.visibleYPos();
      return {
        start: y,
        extent: y + 0.5
      };
    } else {
      let note = notes[0];
      let y = note.visibleYPos();
      return {
        start: y,
        extent: y - 0.5
      };
    }
  }

 /**
  *  Returns the y coordinate of the stem end (in staff spaces), as if the event is not beamed
  *  > 1 notes and stem direction are taken into account
  */
  getStemEnd() {
    let notes = this.notes;
    let note;
    let spacePerFlag = 1;

    if (notes.length > 1) {
      note = this.stem.direction === 'up' ? notes[0] : notes[notes.length - 1];
    } else {
      note = notes[0];
    }

    // Special case: whole notes don't have a stem
    if (this.noteValue === 'w') {
      return note.visibleYPos() + this.stem.direction === 'up' ? -0.5 : 0.5;
    }

    // Manual stem length
    // if () {
    //
    // }

    // Stem up
    if (this.stem.direction === 'up') {
      // Default is one octave = 3.5 spaces, but shorter stem when note is above the middle staff line
      let result = note.visibleYPos() + (note.staffLine < 0 ? -2.7 : -3.5);
      // Add space for each flag/beam after the first two
      if (this.flags > 2) {
        result -= (this.flags - 2) * spacePerFlag;
      }
      // Stem goes at least to the middle staff line
      if (note.staffLine > 0) {
        result = Math.min(result, 0);
      }
      return result;
    } else {
      // Stem down
      // Default is one octave = 3.5 spaces, but shorter stem when note is below the middle staff line
      let result = note.visibleYPos() + (note.staffLine > 0 ? 2.7 : 3.5);
      // Add space for each flag/beam after the first two
      if (this.flags > 2) {
        result += (this.flags - 2) * spacePerFlag;
      }
      // Stem goes at least to the middle staff line
      if (note.staffLine < 0) {
        result = Math.max(result, 0);
      }
      return result;
    }
  }

  draw(ctx) {
    ctx.save();
    ctx.lineCap = 'butt';
    ctx.fillStyle = this.defaultColor();

    let staffSpace = this.score.staffSize * 0.25;
    let stemThickness = staffSpace * (this.score.engravingDefaults.stemThickness || 0.12);
    let beamThickness = staffSpace * (this.score.engravingDefaults.beamThickness || 0.5);
    let beamSpacing = staffSpace * (this.score.engravingDefaults.beamSpacing || 0.25);
    let xAdjust = stemThickness * 0.5;

    // Draw notes and collect info about y positions
    let min = this.notes[0].staffLine;
    let max = this.notes[0].staffLine;
    for (let note of this.notes) {
      min = Math.min(min, note.staffLine);
      max = Math.max(max, note.staffLine);
      note.draw(ctx);
    }

    // Draw leger lines if needed
    if (min <= -3) {
      min = Math.ceil(min);
      ctx.beginPath();
      for (let i = -3; i >= min; i--) {
        ctx.moveTo(this.scoreX - 2, this.staffY + i * staffSpace);
        ctx.lineTo(this.scoreX + 10, this.staffY + i * staffSpace);
      }
      ctx.lineWidth = staffSpace * (this.score.engravingDefaults.legerLineThickness || 0.16);
      ctx.stroke();
    }
    if (max >= 3) {
      max = Math.floor(max);
      ctx.beginPath();
      for (let i = 3; i <= max; i++) {
        ctx.moveTo(this.scoreX - 2, this.staffY + i * staffSpace);
        ctx.lineTo(this.scoreX + 10, this.staffY + i * staffSpace);
      }
      ctx.lineWidth = staffSpace * (this.score.engravingDefaults.legerLineThickness || 0.16);
      ctx.stroke();
    }

    // Draw stem and flags
    ctx.lineWidth = stemThickness;
    if (this.noteValue === 'w') {
      // No stem
    } else if (this.stem.direction === 'up') {
      let x = this.scoreX + (this.stemX || 0);
      // Stem
      // Small adjustment: if there is a beam and it is sloped, the outermost stem will be a little too long.
      // We compensate by shortening the stem. Instead of calculating an exact amount,
      // we trust that the stem end will be covered by the beam anyway.
      let stemEnd = this.stem.end + (this.beam ? 0.25 : 0);
      ctx.beginPath();
      ctx.moveTo(x, this.staffY + staffSpace * this.stem.start);
      ctx.lineTo(x, this.staffY + staffSpace * stemEnd);
      ctx.stroke();
      // Flag
      if (!this.beam) {
        x -= xAdjust; // correct?
        switch (this.noteValue) {
          case '8': ctx.fillText('\uE240', x, this.staffY + staffSpace * this.stem.end); break;
          case '16': ctx.fillText('\uE242', x, this.staffY + staffSpace * this.stem.end); break;
          case '32': ctx.fillText('\uE244', x, this.staffY + staffSpace * this.stem.end); break;
          case '64': ctx.fillText('\uE246', x, this.staffY + staffSpace * this.stem.end); break;
        }
      }
    } else {
      let x = this.scoreX + (this.stemX || 0);
      // Stem
      let stemEnd = this.stem.end - (this.beam ? 0.25 : 0);
      ctx.beginPath();
      ctx.moveTo(x, this.staffY + staffSpace * this.stem.start);
      ctx.lineTo(x, this.staffY + staffSpace * stemEnd);
      ctx.stroke();
      // Flag
      if (!this.beam) {
        x -= xAdjust; // correct?
        switch (this.noteValue) {
          case '8': ctx.fillText('\uE241', x, this.staffY + staffSpace * this.stem.end); break;
          case '16': ctx.fillText('\uE243', x, this.staffY + staffSpace * this.stem.end); break;
          case '32': ctx.fillText('\uE245', x, this.staffY + staffSpace * this.stem.end); break;
          case '64': ctx.fillText('\uE247', x, this.staffY + staffSpace * this.stem.end); break;
        }
      }
    }

    // Draw beams
    if (this.beam && this.beam.events[0] === this) {
      let beamEvents = this.beam.events;
      let firstEvent = beamEvents[0];
      let lastEvent = beamEvents[beamEvents.length - 1];
      let firstX = firstEvent.x + (firstEvent.stemX || 0);
      let lastX = lastEvent.x + (lastEvent.stemX || 0);
      let firstY = this.staffY + staffSpace * firstEvent.stem.end;
      let lastY = this.staffY + staffSpace * lastEvent.stem.end;
      let slope = (lastY - firstY) / (lastX - firstX);

      if (this.stem.direction === 'down') firstY -= beamThickness;

      // console.log('Drawing beam: ', this.beam, firstY, lastY, this.staffY, slope, firstX, lastX);
      for (let level of this.beam.beams) {
        // console.log('  Level: ', level);
        for (let beam of level) {
          let event1 = beam[0];
          let event2 = beam[beam.length - 1];
          let x1 = event1.x + (event1.stemX || 0) - xAdjust;
          let x2 = event2.x + (event2.stemX || 0) + xAdjust;

          // Beamlet
          // TODO: make sure they always point in the correct direction
          if (event1 === event2) {
            if (event1 === firstEvent) {
              x2 = x1 + staffSpace * 1.1;
            } else {
              x1 = x2 - staffSpace * 1.1;
            }
          }

          let y1 = firstY + (x1 - firstX) * slope;
          let y2 = firstY + (x2 - firstX) * slope;

          // console.log('  Drawing [' + x1 + ' ' + y1 + ' ' + x2 + ' ' + y2 + ']');

          ctx.beginPath();
          ctx.moveTo(x1, y1);
          ctx.lineTo(x2, y2);
          ctx.lineTo(x2, y2 + beamThickness);
          ctx.lineTo(x1, y1 + beamThickness);
          ctx.closePath();
          ctx.fill();
        }
        if (this.stem.direction === 'up') {
          firstY += beamThickness + beamSpacing;
        } else {
          firstY -= beamThickness + beamSpacing;
        }
      }
    }
    ctx.restore();
  }

  toSVG(svg) {
    svg.fill(this.defaultColor());
    svg.addClass('ScoreNoteChord');
    svg.addClass('voice-' + this.voice.id);
    // lineCap = 'butt';

    let score = this.score;
    let staffSpace = score.staffSize * 0.25;
    let stemThickness = staffSpace * (score.engravingDefaults.stemThickness || 0.12);
    let beamThickness = staffSpace * (score.engravingDefaults.beamThickness || 0.5);
    let beamSpacing = staffSpace * (score.engravingDefaults.beamSpacing || 0.25);
    let xAdjust = stemThickness * 0.5;

    // Draw notes and collect info about y positions
    let min = this.notes[0].staffLine;
    let max = this.notes[0].staffLine;
    for (let note of this.notes) {
      min = Math.min(min, note.staffLine);
      max = Math.max(max, note.staffLine);
      let group = svg.group();
      note.toSVG(group);
      group.addClass('ScoreNote');
      group.addClass('voice-' + this.voice.id);
      // Add references
      note.svgElement = group;
      score.itemsBySVGID[group.id()] = note;
      // Attach message handlers
      group.on('mouseenter', score.handleMouseEnter.bind(score));
      group.on('mouseleave', score.handleMouseLeave.bind(score));
      group.on('click', score.handleClick.bind(score));
    }

    // Draw syllables
    for (let syllable of this.syllables) {
      let group = svg.group();
      syllable.toSVG(group);
      group.addClass('ScoreSyllable');
      group.addClass('ScoreItem');
      // Add references
      syllable.svgElement = group;
      score.itemsBySVGID[group.id()] = syllable;
      // Attach message handlers
      group.on('mouseenter', score.handleMouseEnter.bind(score));
      group.on('mouseleave', score.handleMouseLeave.bind(score));
      group.on('click', score.handleClick.bind(score));
    }

    // Draw leger lines if needed
    if (min <= -3) {
      min = Math.ceil(min);
      let legerLines = svg.group().stroke({width: staffSpace * (this.score.engravingDefaults.legerLineThickness || 0.16)});
      let ext = staffSpace * (this.score.engravingDefaults.legerLineExtension || 0.4);
      for (let i = -3; i >= min; i--) {
        legerLines.line(this.scoreX - ext, this.staffY + i * staffSpace, this.scoreX + this.innerWidth() * staffSpace + ext, this.staffY + i * staffSpace);
      }
    }
    if (max >= 3) {
      max = Math.floor(max);
      let legerLines = svg.group().stroke({width: staffSpace * (this.score.engravingDefaults.legerLineThickness || 0.16)});
      let ext = staffSpace * (this.score.engravingDefaults.legerLineExtension || 0.4);
      for (let i = 3; i <= max; i++) {
        legerLines.line(this.scoreX - ext, this.staffY + i * staffSpace, this.scoreX + this.innerWidth() * staffSpace + ext, this.staffY + i * staffSpace);
      }
    }

    // Draw stem and flags
    if (this.noteValue === 'w') {
      // No stem for whole notes
    } else if (this.stem.direction === 'up') {
      let x = this.scoreX + (this.stemX || 0);
      // Stem
      // Small adjustment: if there is a beam and it is sloped, the outermost stem will be a little too long.
      // We compensate by shortening the stem. Instead of calculating an exact amount,
      // we trust that the stem end will be covered by the beam anyway.
      let stemEnd = this.stem.end + (this.beam ? 0.25 : 0);
      let line = svg.line(x, this.staffY + staffSpace * this.stem.start, x, this.staffY + staffSpace * stemEnd).addClass('stem');
      line.stroke({width: stemThickness});
      // Flag
      if (!this.beam) {
        x -= xAdjust; // correct?
        let y = this.staffY + staffSpace * this.stem.end;
        switch (this.noteValue) {
          case '8': svg.plain('\uE240').x(x).attr('y', y).font({family: null, size: null}); break;
          case '16': svg.plain('\uE242').x(x).attr('y', y).font({family: null, size: null}); break;
          case '32': svg.plain('\uE244').x(x).attr('y', y).font({family: null, size: null}); break;
          case '64': svg.plain('\uE246').x(x).attr('y', y).font({family: null, size: null}); break;
          case '128': svg.plain('\uE248').x(x).attr('y', y).font({family: null, size: null}); break;
          case '256': svg.plain('\uE24A').x(x).attr('y', y).font({family: null, size: null}); break;
        }
      }
    } else {
      let x = this.scoreX + (this.stemX || 0);
      // Stem
      let stemEnd = this.stem.end - (this.beam ? 0.25 : 0);
      let line = svg.line(x, this.staffY + staffSpace * this.stem.start, x, this.staffY + staffSpace * stemEnd).addClass('stem');
      line.stroke({width: stemThickness});
      // // Flag
      if (!this.beam) {
        x -= xAdjust; // correct?
        let y = this.staffY + staffSpace * this.stem.end;
        switch (this.noteValue) {
          case '8': svg.plain('\uE241').x(x).attr('y', y).font({family: null, size: null}); break;
          case '16': svg.plain('\uE243').x(x).attr('y', y).font({family: null, size: null}); break;
          case '32': svg.plain('\uE245').x(x).attr('y', y).font({family: null, size: null}); break;
          case '64': svg.plain('\uE247').x(x).attr('y', y).font({family: null, size: null}); break;
          case '128': svg.plain('\uE249').x(x).attr('y', y).font({family: null, size: null}); break;
          case '256': svg.plain('\uE24B').x(x).attr('y', y).font({family: null, size: null}); break;
        }
      }
    }

    // Draw beams
    if (this.beam && this.beam.events[0] === this) {
      let beamEvents = this.beam.events;
      let firstEvent = beamEvents[0];
      let lastEvent = beamEvents[beamEvents.length - 1];
      let firstX = firstEvent.x + (firstEvent.stemX || 0);
      let lastX = lastEvent.x + (lastEvent.stemX || 0);
      let firstY = this.staffY + staffSpace * firstEvent.stem.end;
      let lastY = this.staffY + staffSpace * lastEvent.stem.end;
      let slope = (lastY - firstY) / (lastX - firstX);

      if (this.stem.direction === 'down') firstY -= beamThickness;

      // console.log('Drawing beam: ', this.beam, firstY, lastY, this.staffY, slope, firstX, lastX);
      for (let level of this.beam.beams) {
        // console.log('  Level: ', level);
        for (let beam of level) {
          let event1 = beam[0];
          let event2 = beam[beam.length - 1];
          let x1 = event1.x + (event1.stemX || 0) - xAdjust;
          let x2 = event2.x + (event2.stemX || 0) + xAdjust;

          // Beamlet
          // TODO: make sure they always point in the correct direction
          if (event1 === event2) {
            if (event1 === firstEvent) {
              x2 = x1 + staffSpace * 1.1;
            } else {
              x1 = x2 - staffSpace * 1.1;
            }
          }

          let y1 = firstY + (x1 - firstX) * slope;
          let y2 = firstY + (x2 - firstX) * slope;

          // console.log('  Drawing [' + x1 + ' ' + y1 + ' ' + x2 + ' ' + y2 + ']');

          svg.path([
            'M', x1, y1,
            'L', x2, y2,
            'L', x2, y2 + beamThickness,
            'L', x1, y1 + beamThickness,
            'Z']).fill();
        }
        if (this.stem.direction === 'up') {
          firstY += beamThickness + beamSpacing;
        } else {
          firstY -= beamThickness + beamSpacing;
        }
      }
    }
  }

  toString() {
    return `[ScoreNoteChord (${this.notes.length})]`;
  }
}

import knowledge from '../knowledge.js';
import ScoreItem from './score-item.js';

export default class ScoreNote extends ScoreItem {
  constructor(item, score, params) {
    super(item, score, params);
    let coord = item.pitch.toCoord();
    this.coord = coord;
    if (this.voice.kind === 'single-drum') {
      this.staffLine = 0;
    } else if (this.clef) {
      switch (this.clef.kind) {
        case 'g': this.staffLine = 20.5 - (coord[0] * 0.5); break;
        case 'c': this.staffLine = 17.5 - (coord[0] * 0.5); break;
        case 'f': this.staffLine = 14.5 - (coord[0] * 0.5); break;
      }
      this.staffLine += this.clef.octave * 3.5;
    } else {
      // Assume g clef
      this.staffLine = 20.5 - (coord[0] * 0.5);
    }
  }

  visibleYPos() {
    return this.staffLine;
  }

  set notehead(notehead) {
    this._notehead = {
      name: notehead,
      bbox: this.score.musicFontMetadata.glyphBBoxes[notehead],
      anchors: this.score.musicFontMetadata.glyphsWithAnchors[notehead]
    };
  }

  get notehead() {
    return this._notehead.name;
  }

  boundingBox() {
    // TODO: don't hardcode
    let bbox = this._notehead.bbox;
    return {
      left: bbox.bBoxSW[0],
      top: -bbox.bBoxNE[1],
      right: bbox.bBoxNE[0],
      bottom: -bbox.bBoxSW[1]
    };
    //   (let ((metrics (svref *glyph-data* (notehead-to-smufl-index (notehead self)))))
    // (values (- (glyph-left metrics) (glyph-notehead-origin-x metrics))
    //         (glyph-top metrics)
    //         (- (glyph-right metrics) (glyph-notehead-origin-x metrics))
    //         (glyph-bottom metrics)))
  }

  draw(ctx) {
    let snc = this.scoreNoteChord;
    // console.log('Drawing notehead at ' + snc.x + ', ' + snc.y);
    let noteY = snc.y + this.score.staffSize * 0.25 * this.visibleYPos();
    let str = knowledge.smuflCodepoints[this.notehead];
    let x = snc.x + this.noteX;
    ctx.fillText(str, x, noteY);
    let dotX = x + 10; // TODO
    for (let d = 0; d < snc.dots; d++) {
      ctx.fillText('\uE1E7', dotX, noteY);
      dotX += 7; // TODO
    }
  }

  toSVG(svg) {
    let snc = this.scoreNoteChord;
    let staffSize = this.score.staffSize;
    let noteY = snc.y + staffSize * 0.25 * this.visibleYPos();
    let x = snc.x + this.noteX;
    svg.smuflGlyph(this.notehead, x, noteY, staffSize);
    if (snc.dots) {
      let dotW = staffSize * 0.25 * this.score.musicFontMetadata.glyphBBoxes['augmentationDot'].bBoxNE[0];
      let dotX = snc.x + snc.dotX + dotW;
      let dotY = noteY + (this.staffLine % 1 !== 0 ? 0 : staffSize * -0.125);
      for (let d = 0; d < snc.dots; d++) {
        svg.smuflGlyph('augmentationDot', dotX, dotY, staffSize);
        dotX += dotW * 2;
      }
    }

    // Accidental
    if (this.displayedAccidental !== undefined) {
      svg.smuflGlyph(knowledge.accidentals[this.displayedAccidental], x + this.accidentalX, noteY, staffSize);
    }
  }

  toString() {
    return `[ScoreNote ${this.item.pitch.scientific()}]`;
  }
}

import ScoreEvent from './score-event.js';

export default class ScoreRest extends ScoreEvent {
  innerWidth() {
    return 1; // TODO
  }

  draw(ctx) {
    ctx.fillStyle = this.defaultColor();
    let staffSpace = this.score.staffSize * 0.25;
    switch (this.noteValue) {
      case 'w':
        ctx.fillText(Math.abs(this.staffLine) > 2 ? '\uE4F4' : '\uE4E3', this.x, this.y - staffSpace);
        break;
      case 'h':
        ctx.fillText(Math.abs(this.staffLine) > 2 ? '\uE4F5' : '\uE4E4', this.x, this.y);
        break;
      case 'q': ctx.fillText('\uE4E5', this.x, this.y); break;
      case '8': ctx.fillText('\uE4E6', this.x, this.y); break;
      case '16': ctx.fillText('\uE4E7', this.x, this.y); break;
      case '32': ctx.fillText('\uE4E8', this.x, this.y); break;
      case '64': ctx.fillText('\uE4E9', this.x, this.y); break;
      case '128': ctx.fillText('\uE4EA', this.x, this.y); break;
      case '256': ctx.fillText('\uE4EB', this.x, this.y); break;
    }

    let dotX = this.x + staffSpace * 1.3; // TODO

    // Draw augmentation dots
    for (let d = 0; d < this.dots; d++) {
      ctx.fillText('\uE1E7', dotX, this.y);
      dotX += staffSpace; // TODO
    }
  }

  toSVG(svg) {
    svg.addClass('voice-' + this.voice.id);
    let staffSpace = this.score.staffSize * 0.25;
    switch (this.noteValue) {
      case 'w':
        svg.plain(Math.abs(this.staffLine) > 2 ? '\uE4F4' : '\uE4E3').x(this.x).attr('y', this.y - staffSpace).font({family: null, size: null});
        break;
      case 'h':
        svg.plain(Math.abs(this.staffLine) > 2 ? '\uE4F5' : '\uE4E4').x(this.x).attr('y', this.y).font({family: null, size: null});
        break;
      case 'q': svg.plain('\uE4E5').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
      case '8': svg.plain('\uE4E6').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
      case '16': svg.plain('\uE4E7').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
      case '32': svg.plain('\uE4E8').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
      case '64': svg.plain('\uE4E9').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
      case '128': svg.plain('\uE4EA').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
      case '256': svg.plain('\uE4EB').x(this.x).attr('y', this.y).font({family: null, size: null}); break;
    }

    let dotX = this.x + staffSpace * 1.3; // TODO

    // Draw augmentation dots
    for (let d = 0; d < this.dots; d++) {
      svg.plain('\uE1E7').x(dotX).attr('y', this.y).font({family: null, size: null});
      dotX += staffSpace; // TODO
    }
  }
}

import ScoreDetail from './score-detail.js';

export default class ScoreSyllable extends ScoreDetail {
  constructor(item, score, params) {
    super(item, score, params);
    this.verse = this.verse || 0;
    this.text = item.text;
    this.hyphen = item.hyphen;
    this.extender = item.extender;
  }

  visibleYPos() {
    return 6 + this.verse * 3;
  }

  toSVG(svg) {
    let staffSize = this.score.staffSize;
    let staffSpace = staffSize * 0.25;
    let owner = this.owner;
    let y = owner.y + staffSpace * this.visibleYPos();
    let text = this.text;
    let font = {family: 'Times New Roman', size: 13};
    let x = owner.x;

    if (owner.tiedFrom) {
      font.anchor = 'start';
    } else {
      x += staffSpace * owner.innerWidth() * 0.5;
      font.anchor = 'middle';
    }
    svg.plain(text).x(x).attr('y', y).font(font);

    if (this.hyphen) {
      svg.plain('-').x(x + 5).attr('y', y).font({family: 'Times New Roman', size: 13, anchor: 'start'});
    }
  }
}

import ScoreMeta from './score-meta.js';

export default class ScoreTie extends ScoreMeta {
  updatePosition() {
    let staffSpace = this.score.staffSize * 0.25;
    let note = this.fromNote || this.toNote;
    this.x = this.x1 + (this.xOffset || 0);
    this.y = this.staffY + staffSpace * ((this.yOffset || 0) + note.visibleYPos());
  }

  updateBounds() {
    let staffSpace = this.score.staffSize * 0.25;
    let width = this.x2 - this.x1;
    let thickness = staffSpace * (width < (2 * staffSpace) ? 0.23 : 0.36);
    let height = staffSpace * tieHeight(width / staffSpace) + thickness;
    this.bounds = {
      minX: this.x1,
      minY: this.y + (this.direction === 'up' ? -height : 0),
      maxX: this.x2,
      maxY: this.y + (this.direction === 'up' ? 0 : height)
    };
  }

  draw(ctx) {
    ctx.save();
    ctx.fillStyle = this.defaultColor();
    let staffSpace = this.score.staffSize * 0.25;
    let width = this.x2 - this.x1;
    let height = staffSpace * tieHeight(width / staffSpace);
    let cw = width * 0.7;
    let cx1 = (width - cw) * 0.5;
    let cx2 = width - cx1;
    let thickness = staffSpace * (width < (2 * staffSpace) ? 0.23 : 0.36);
    let end = 0.043 * staffSpace;
    let endY = end;
    let x = this.x;
    let y = this.y;
    if (this.direction === 'up') {
      y -= staffSpace * 0.5;
      height = -height;
      endY = -endY;
      thickness = -thickness;
    } else {
      y += staffSpace * 0.5;
    }
    ctx.beginPath();
    ctx.moveTo(x + end, y);
    ctx.bezierCurveTo(x + cx1, y + height, x + cx2, y + height, x + width - end, y);
    ctx.lineTo(x + width, y + endY);
    ctx.bezierCurveTo(x + cx2, y + height + thickness, x + cx1, y + height + thickness, x, y + endY);
    ctx.closePath();
    ctx.fill();

    ctx.restore();
  }

  toSVG(svg) {
    let staffSpace = this.score.staffSize * 0.25;
    let width = this.x2 - this.x1;
    let height = staffSpace * tieHeight(width / staffSpace);
    let cw = width * 0.7;
    let cx1 = (width - cw) * 0.5;
    let cx2 = width - cx1;
    let thickness = staffSpace * (width < (2 * staffSpace) ? 0.23 : 0.36);
    let end = 0.043 * staffSpace;
    let endY = end;
    let x = this.x;
    let y = this.y;
    if (this.direction === 'up') {
      y -= staffSpace * 0.5;
      height = -height;
      endY = -endY;
      thickness = -thickness;
    } else {
      y += staffSpace * 0.5;
    }
//    let path = svg.path(['M' + (x + end) + ' ' + y + ' C' + (x + cx) + (y + height)
    let path = svg.path([
      'M', x + end, y,
      'C', x + cx1, y + height, x + cx2, y + height, x + width - end, y,
      'L', x + width, y + endY,
      'C', x + cx2, y + height + thickness, x + cx1, y + height + thickness, x, y + endY,
      'Z']);
    path.fill();
  }
}

function tieHeight(w) {
  return Math.min(Math.max(0.28, 0.08 * w), 1.71);
}

import ScoreMeta from './score-meta.js';
// import knowledge from '../knowledge.js';

export default class ScoreTime extends ScoreMeta {
  // constructor(item, score, params) {
  //   super(item, score, params);
  // }

  innerWidth() {
    return 2; // TODO
  }

  minSpace(nextItem) {
    return 1.5;
  }

  toSVG(svg) {
    let staffSpace = this.score.staffSize * 0.25;
    let n = this.item.num;
    let d = this.item.denom;
    // let smufl = knowledge.smuflCodepoints;
    if (this.abbreviate && n === 4 && d === 4) {
      svg.smuflGlyph('timeSigCommon', this.x, this.y);
    } else if (this.abbreviate && this.num === 2 && this.denom === 2) {
      svg.smuflGlyph('timeSigCutCommon', this.x, this.y);
    } else {
      svg.smuflGlyph('timeSig' + n, this.x, this.y - staffSpace);
      svg.smuflGlyph('timeSig' + d, this.x, this.y + staffSpace);
    }
  }
}

import ScoreMeta from './score-meta.js';
import Fraction from 'fraction.js';

export default class ScoreTuplet extends ScoreMeta {
  constructor(item, score, params) {
    super(item, score, params);
    let fraction = new Fraction(1).div(item.scale);
    let text = fraction.n + ':' + fraction.d;
    if (['3:2', '2:3', '3:2', '4:3', '5:4', '6:4', '7:4', '7:6', '7:8', '9:8'].indexOf(text) >= 0) {
      text = text.substr(0, 1);
    }
    this.text = text;
  }

  get floating() {
    return true;
  }

  toSVG(svg) {
    let staffSpace = this.score.staffSize * 0.25;
    // let x = this.x;
    // let y = this.y;
    // let thickness = this.score.engravingDefaults.bracketThickness || 0.5 * staffSpace;
    // let line = svg.line(this.x, this.y - 20, this.x + 10, this.y - 20);
    // svg.smuflGlyph(, this.x + 20, this.y - 20, this.score.staffSize);
    // line.stroke({width: thickness});
    let font = {family: 'Palatino, "Palatino Linotype", "URW Palladio"', size: staffSpace * 1.5, style: 'italic'};
    svg.plain(this.text).x(this.x + 20).attr('y', this.y + 10).font(font);
  }
}

export default class StaffSystem {
  constructor(score, num, params = {}) {
    this.score = score;
    this.num = num;
    this.x = 0;
    this.y = 40;
    this.indent = params.indent || 0;
    this.staffSize = params.staffSize || score.staffSize || 28;
    this.staffWidth = params.staffWidth || score.width;
    this.staffLineWidth = params.staffLineWidth || 0.8;
    this.stemThickness = params.stemThickness || 1.0;
    this.staffDistance = params.staffDistance || score.staffDistance || 80;
    this.staves = params.staves;
  }

  itemAt(x, y) {
    for (let item of this.items) {
      let b = item.bounds;
      if (x >= b.minX && x <= b.maxX && y >= b.minY && y <= b.maxY) return item;
    }
  }

  draw(ctx) {
    let x1 = this.x + this.indent;
    let x2 = this.staffWidth;
    let y = this.y;
    let delta = this.staffSize * 0.25;
    ctx.save();
    ctx.lineCap = 'butt';
    ctx.lineWidth = this.score.staffLineWidth;
    ctx.beginPath();
    for (let i = -2; i <= 2; i++) {
      ctx.moveTo(x1, y + (i * delta));
      ctx.lineTo(x2, y + (i * delta));
    }
    ctx.stroke();

    for (let item of this.items) {
      item.draw(ctx);
    }
    // for (let i = 0; i < 5; i++) {
    //   this.items[i].draw(ctx);
    // }

    ctx.restore();
  }

  toSVG(svg) {
    let score = this.score;
    let x1 = this.x + this.indent;
    let x2 = this.staffWidth;
    let y = this.y;
    let delta = this.staffSize * 0.25;

    // Create SVG group for the staff system, with a sane ID
    let staffSystem = svg.group().id('staff-system-' + this.num).translate(0, y);

    // Draw staff lines
    for (let staff = 0; staff < this.staves.length; staff++) {
      let staffY = staff * this.staffDistance; // TODO
      let staffLines = staffSystem.group().id('staff-lines');
      let staffLineWidth = (score.engravingDefaults.staffLineWidth || 0.13) * delta;
      staffLines.attr('stroke-width', staffLineWidth);
      let y1 = staffY + delta * (this.staves[staff].staffLines - 1) * -0.5;
      for (let i = 0; i < this.staves[staff].staffLines; i++) {
        staffLines.line(x1, y1 + (i * delta), x2, y1 + (i * delta)).stroke().id(null);
      }
    }

    // Draw score items
    for (let item of this.items) {
      // Wrap every ScoreItem in a SVG group
      let group = staffSystem.group();
      group.addClass('ScoreItem');
      group.addClass(item.constructor.name);
      if (item.item && item.item.className) group.addClass(item.item.className);
      // Call function in sub class for actual SVG generation
      item.toSVG(group);
      // Attach message handlers
      group.on('mouseenter', score.handleMouseEnter.bind(score));
      group.on('mouseleave', score.handleMouseLeave.bind(score));
      group.on('click', score.handleClick.bind(score));
      // Set references from SVG object to ScoreItem and back
      // NB: as we cannot store object references in the SVG,
      //     we use a key in the score as a lookup table
      item.svgElement = group;
      score.itemsBySVGID[group.id()] = item;
    }

    return staffSystem;
  }
}

